/* eslint-disable no-redeclare */
const Html2PDFmake = require('Html2PDFmake')
const ReportFormat = require('ReportFormat')
const ReportUtils = require('ReportUtils')
const path = require('path')

class PdfFormat extends ReportFormat {
  constructor (options) {
    super(['pdf'], options === undefined ? PdfFormat.DefaultOptions() : options)
    this._definition = {
      content: [],
      footer: this.options.footer,
      header: this.options.header,
      images: {},
      info: this.options.metadata,
      pageMargins: this.options.pageMargins,
      pageSize: this.options.pageSize,
      styles: this.options.styles,
    } 
    if (this.options.watermark.enabled) {
      this._definition.watermark = { text: this.options.watermark.text, color: 'grey', opacity: 0.1, bold: true, italics: false, fontSize: 128 }
    }
    this._definition.header = {
      image: 'logo',
      absolutePosition: {
        x: this.mm2pt(150),
        y: this.mm2pt(0)
      },
      fit: [this.mm2pt(60), this.mm2pt(60)]
    }
    this._definition.images.logo = 'data:image/png;base64,'
  }

  endSectionHeader () {
    this._definition.content.push('')
  }

  endTitlepage () {
    this._definition.content.push(
      {
        text: '', pageBreak: 'after'
      }
    )
  }

  insertPageBreak (pos) {
    var p = pos === undefined ? 'before' : pos
    if (this._definition.content.length > 0) {
      var node = this._definition.content[this._definition.content.length - 1]
      if (typeof node === 'object') {
        if (Array.isArray(node.text)) {
          node.pageBreak = p
        } else if (node.table !== undefined) {
          this._definition.content.push({ text: '', pageBreak: p })
        }
      } else if (typeof node === 'string') {
        var n = {
          text: node,
          pageBreak: p
        }
        this._definition.content[this._definition.content.length - 1] = n
      }
    }
  }

  render () {
    return new Promise((resolve, reject) => {
      try {
        var fonts = {
          Roboto: {
            normal: path.join(this.options.folder.fonts, 'Roboto-Regular.ttf'),
            bold: path.join(this.options.folder.fonts, 'Roboto-Medium.ttf'),
            italics: path.join(this.options.folder.fonts, 'Roboto-Italic.ttf'),
            bolditalics: path.join(this.options.folder.fonts, 'Roboto-MediumItalic.ttf')
          }
        }
        var PdfPrinter = require('pdfmake')
        var printer = new PdfPrinter(fonts)
        var pdfDoc = printer.createPdfKitDocument(this._definition)

        var chunks = []
        var result = null
        pdfDoc.on('data', function (chunk) {
          chunks.push(chunk)
        })
        pdfDoc.on('end', function () {
          result = Buffer.concat(chunks)
          resolve('data:application/pdf;base64,' + result.toString('base64'))
        })
        pdfDoc.end()
      } catch (err) {
        reject(err)
      }
    })
  }

  renderChangeIndex () {

  }

  renderListOfSignatures () {
    var n = {
      table: {
        headerRows: 1,
        heights: 10,
        widths: [
          ReportUtils.mm2pt(31),
          ReportUtils.mm2pt(44),
          ReportUtils.mm2pt(27),
          ReportUtils.mm2pt(58)
        ],
        body: [
          ['Position', 'Name', 'Datum', 'Unterschrift']
        ]
      },
      absolutePosition: {
        x: ReportUtils.mm2pt(this.options.los.absolutePosition.x),
        y: ReportUtils.mm2pt(this.options.los.absolutePosition.y + 10)
      }
    }
    this.options.document.signatures.forEach(item => {
      if (item.enabled) {
        n.table.body.push([
          item.position,
          item.name,
          item.date,
          ''
        ])
      }
    })
    this._definition.content.push(n)
  }

  renderTitlepage () {
    this._definition.content.push(
      {
        image: 'logo',
        absolutePosition: {
          x: this.mm2pt(150),
          y: this.mm2pt(0)
        },
        fit: [this.mm2pt(60), this.mm2pt(60)]
      }
    )
    this._definition.content.push(
      {
        text: this.options.system === undefined ? 'INTIS Induktives Ladesystem' : this.options.system,
        style: 'documentTitle',
        absolutePosition: {
          x: ReportUtils.mm2pt(20),
          y: ReportUtils.mm2pt(70)
        }
      }
    )
    this._definition.content.push(
      {
        text: this.options.document.number === undefined ? 'Document Number' : this.options.document.number,
        style: 'documentNumber',
        absolutePosition: {
          x: ReportUtils.mm2pt(20),
          y: ReportUtils.mm2pt(80)
        }
      }
    )
    this._definition.content.push(
      {
        text: this.options.document.type === undefined ? 'Document Typ' : this.options.document.type,
        style: 'documentTitle',
        absolutePosition: {
          x: ReportUtils.mm2pt(20),
          y: ReportUtils.mm2pt(105)
        }
      }
    )
    this._definition.content.push(
      {
        text: this.options.document.title === undefined ? 'Document Title' : this.options.document.title,
        style: 'documentTitle',
        absolutePosition: {
          x: ReportUtils.mm2pt(20),
          y: ReportUtils.mm2pt(135)
        }
      }
    )
    this._definition.content.push(
      {
        text: this.t('titlepage.revision', { revision: this.options.document.revision === undefined ? 'Document Revision' : this.options.document.revision }),
        fontSize: 14,
        alignment: 'center',
        absolutePosition: {
          x: ReportUtils.mm2pt(20),
          y: ReportUtils.mm2pt(145)
        }
      }
    )
  }

  startChangeIndex () {
    this._definition.content.push(
      {
        text: this.t('changeindex'),
        style: 'heading1'
      }
    )
  }

  startDocument () {
    var pageNumber = this.t('footer.page')
    var copyrightNotice = this.options.document.copyright
    var number = this.options.document.number
    var revision = this.options.document.revision
    this._definition.footer = function (currentPage, pageCount) {
      return {
        columns: [
          {
            width: '26%',
            text: [
              {
                text: number
              }, {
                text: '\n'
              }, {
                text: revision
              }
            ],
            style: 'pageFooterLeft'
          },
          {
            width: '48%',
            text: [
              {
                // text: currentPage.toString() + ' of ' + pageCount, alignment: 'center'
                text: pageNumber.replace('{{page}}', currentPage).replace('{{pageCount}}', pageCount)
              },
              {
                text: '\n'
              },
              {
                text: copyrightNotice
              }
            ],
            style: 'pageFooter'
          }
        ]
      }
    }
    // o.header = 'Simple header'
  }

  startDocumentBody () {
    this._definition.content.push({
      pageBreak: 'before',
      text: ''
    })
  }

  startListOfSignatures () {
    this._definition.content.push(
      {
        text: this.t('listofsignatures'),
        style: 'title',
        absolutePosition: {
          x: ReportUtils.mm2pt(this.options.los.absolutePosition.x),
          y: ReportUtils.mm2pt(this.options.los.absolutePosition.y)
        }
      }
    )
  }

  startListOfTables () {
    this._definition.content.push({
      toc: {
        id: 'lot',
        title: {
          text: this.t('lot')
        },
        numberStyle: { bold: true }
      },
      pageBreak: 'after'
    })
  }

  startSectionHeader (engine, section) {
    const levels = section.number.split('.').length
    var node = {
      text: section.number + '\t' + section.caption,
      tocItem: 'toc',
      style: 'heading' + levels,
      headlineLevel: levels
    }
    if (section.reportblock.options.section !== undefined && section.reportblock.options.section.pageBreak !== undefined && section.reportblock.options.section.pageBreak) {
      node.pageBreak = 'before'
    }
    this._definition.content.push(node)
  }

  startTableOfContents () {
    this._definition.content.push({
      toc: {
        id: 'toc',
        title: {
          text: this.t('toc'),
          style: 'tocTitle'
        },
        numberStyle: { bold: true }
      },
      pageBreak: 'before'
    })
  }

  writeHtml (html, style) {
    if (html === undefined) {
      return
    }
    var html2pdf = new Html2PDFmake(this.options.styles)
    html2pdf.render(html.startsWith('<html>') ? html : '<html>' + html + '</html>', style)
    this._definition.content = this._definition.content.concat(html2pdf.content)
    return html2pdf.noContent
  }

  writeQRcode (data) {
    this._definition.content.push({ qr: data, fit: 50, alignment: 'right' })
  }

  writeTable (table) {
    var node = {
      table: {
        body: [],
        widths: []
      }
    }
    if (!table.border) {
      node.layout = 'noBorders'
    }
    if (table.style.class !== 'default') {
      node.style = table.style.class
    }
    table.horizontalHeader.columns.forEach(col => {
      if (col.width === 'auto') {
        node.table.widths.push('*')
      } else {
        node.table.widths.push(col.width)
      }
    })
    if (table.horizontalHeader.visible) {
      for (var row = 0; row < table.horizontalHeader.rowCount; row++) {
        var columns = []
        for (var col = 0; col < table.horizontalHeader.columnCount; col++) {
          var n = { text: table.horizontalHeader.caption(col, row) }
          if (table.horizontalHeader.style(col)) {
            n.style = table.horizontalHeader.style(col)
          } else {
            if (table.horizontalHeader.defaultStyle) {
              n.style = table.horizontalHeader.defaultStyle
            }
          }
          columns.push(n)
        }
        node.table.body.push(columns)
      }
    }
    for (var row = 0; row < table.rowCount; row++) {
      var columns = []
      for (var col = 0; col < table.columnCount; col++) {
        var n = {
          text: table.data(row, col),
          style: table.cell(row, col).style ? table.cell(row, col).style : 'tableCell'
        }
        var span = table.span(row, col)
        if (span.rows > 1) {
          n.rowSpan = span.rows
        }
        if (span.cols > 1) {
          n.colSpan = span.cols
        }
        columns.push(n)
      }
      node.table.body.push(columns)
    }
    this._definition.content.push(node)
  }

  static DefaultOptions () {
    var o = ReportFormat.DefaultOptions()
    // o.document.copyright = 'Copyright © INTIS 2020 All Rights Reserved – CONFIDENTIAL DOCUMENT – Schutzvermerk ISO 16016 beachten'
    o.document.copyright = 'Copyright © RePoSyD 2020 All Rights Reserved'
    o.filename = 'document.pdf'
    o.folder = {
      fonts: path.join(__dirname, '/../../public/fonts'),
      output: require('os').tmpdir()
    }

    o.language = 'de'
    o.pageSize = ReportUtils.PaperSize.A4
    o.pageMargins = [ReportUtils.mm2pt(25.4), ReportUtils.mm2pt(25.4), ReportUtils.mm2pt(25.4), ReportUtils.mm2pt(25.4)]
    o.pagerOrientation = ReportUtils.PageOrientation.Portrait
    o.styles = {
      defaultStyle: {
        fontSize: 12
      },
      description: {
        fontSize: 12,
        margin: [12, 6, 12, 12]
      },
      documentNumber: {
        alignment: 'center',
        fontSize: 11
      },
      documentTitle: {
        alignment: 'center',
        fontSize: 18,
        bold: true
      },
      heading1: {
        fontSize: 16,
        margin: [0, 12, 0, 6],
        pageBreak: 'before',
        bold: true
      },
      heading2: {
        fontSize: 14,
        margin: [0, 12, 0, 6],
        bold: true
      },
      heading3: {
        fontSize: 14,
        margin: [0, 12, 0, 6],
        bold: true
      },
      heading4: {
        fontSize: 14,
        margin: [0, 12, 0, 6],
        bold: true
      },
      heading5: {
        fontSize: 14,
        margin: [0, 12, 0, 6],
        bold: true
      },
      heading6: {
        fontSize: 14,
        margin: [0, 12, 0, 6],
        bold: true
      },
      pageFooter: {
        fontSize: 8,
        alignment: 'center'
      },
      pageFooterLeft: {
        fontSize: 8,
        alignment: 'left',
        margin: [ReportUtils.mm2pt(25.4), 0, 0, 0]
      },
      pageFooterRight: {
        fontSize: 8,
        alignment: 'right'
      },
      paragraph: {
        fontSize: 12,
        margin: [0, 6, 0, 12]
      },
      tableStandard: {
        alignment: 'center'
      },
      tableCell: {
        fontSize: 10
      },
      tableCellCenter: {
        fontSize: 10,
        alignment: 'center'
      },
      tableHeader: {
        bold: true,
        fontSize: 10,
        fillColor: '#95b3d7'
      },
      tableHeaderCenter: {
        bold: true,
        fontSize: 10,
        alignment: 'center',
        fillColor: '#95b3d7'
      },
      title: {
        fontsize: 16,
        margin: [0, 12, 0, 6],
        bold: true
      },
      toc: {
        fontsize: 12
      },
      tocTitle: {
        fontsize: 20,
        bold: true
      },
      unorderdList: {
        margin: [0, 12, 0, 12]
      }
    }
    return o
  }
}

module.exports = PdfFormat
